import React from 'react';
import { Card, CardActions, CardMedia, CardContent, Button, Typography } from '@material-ui/core';
import ThumbUpAltIcon from '@mui/icons-material/ThumbUpAlt';
import DeleteIcon from '@mui/icons-material/Delete';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import useStyles from './styles';

const Post = ({ post, setCurrentId }) => {
  const classes = useStyles();

  return (
    <Card className={classes.card}>
      <CardMedia className={classes.media} component="img" image={post.selectedFile} alt={post.title} />
      <div className={classes.overlay}>
        <Typography variant="h6">{post.creator}</Typography>
        <Typography variant="body2">{post.createdAt}</Typography>
      </div>
      <div className={classes.overlay2}>
        <Button 
          style={{color: 'white'}} 
          size="small" 
          onClick={() => {setCurrentId(post._id)}}
        >
          <MoreHorizIcon fontSize="default" />
        </Button>

      </div>
      <div className={classes.details}>
        <Typography variant="body2" color="textSecondary">{post.tags.map(tag => `#${tag} `)}</Typography>
      </div>
      <Typography className={classes.title} variant="h5" gutterBottom>{post.title}</Typography>
      <CardContent> 
        <Typography gutterBottom>{post.message}</Typography>
      </CardContent>
      <CardActions className={classes.cardActions}>
        <Button size="small" color="primary" onClick={() => {}}>
          <ThumbUpAltIcon fontSize="small" />
          Like
          {post.linkCount}
        </Button>
        <Button size="small" color="primary" onClick={() => {}}>
          <DeleteIcon fontSize="small"  />
            Delete
        </Button>
      </CardActions>
    </Card>
  );
}

export default Post;
