import { CREATE, FETCH_ALL, UPDATE, DELETE, LIKE } from '../constants/actionTypes';

const reducer = (state = [], action) => {
  switch (action.type) {
    case FETCH_ALL:
      return action.payload;
    case CREATE:
      return [...state, action.payload];
    default:
      return state;
  }
}

export default reducer;
